type StudioAsset = {
    source: 'cloudinary';
    asset: Record<string, any>;
};

export function getRemoteAssetUrl(assetObject: StudioAsset): string | undefined {
    if (assetObject.source === 'cloudinary') {
        return assetObject.asset.derived?.[0]?.secure_url ?? assetObject.asset.secure_url;
    }
    return undefined;
}
